            var downloaded = 0;
            _.each(this.download_status, function(status, entity){
                //if total objects for any entity are not yet known, then can't show the progress
                if(status.total==null)
                {
                    ready_to_show = false;
                    return;    
                }
                else
                {
                    total += status.total;
                    downloaded += status.downloaded;
                }
            });
            
